mapper.select:
insert into board.qna
(qna_course_id,
qna_title,
qna_contents,
qna_user_id,
qna_secret,
qna_attach_id,
qna_insert_date,
qna_update_date
) VALUES (
#{qna_course_id}
,#{qna_title}
,#{qna_contents}
,#{qna_user_id}
,#{qna_secret}
,#{qna_attach_id}
,#{qna_insert_date}
,#{qna_update_date}
) returning qna_no

mapper.update:
UPDATE board.qna
SET qna_delete_yn = 'Y'
, qna_update_date = #{qna_update_date}
WHERE qna_no = #{qna_no}

mapper.select:
INSERT INTO board.qna_reply
(qna_no,
qna_reply_contents,
qna_reply_user_id,
qna_reply_user_role,
qna_reply_insert_date,
qna_reply_update_date,
qna_delete_yn
) VALUES (
#{qna_no}
,#{qna_reply_contents}
,#{qna_reply_user_id}
,#{qna_reply_user_role}
,#{qna_reply_insert_date}
,#{qna_reply_update_date}
,'N'
) returning qna_reply_no
